INSERT INTO `doulist` VALUES ('10278', '简媜', '0', 'may28', '139', '0', '<p class=\"indent\">　　  简媜，1961年生于台湾省宜兰县，台大中文系毕业，曾获吴鲁芹散文奖、时报文学奖等。是《台湾文学经典》最年轻的入选者，也是台湾最无争议的实力派女作家。著有《水问》、《只缘身在此山中》、《私房书》、《下午茶》、《女儿红》、《胭脂盆地》等十余种散文集。（<a href=\"http://www.douban.com/link2?url=http%3A//b42783.xici.net\" target=\"_blank\" rel=\"nofollow\">http://b42783.xici.n<wbr>et</wbr></a>）<br /></p>', '2005-12-10 19:18:53', '2010-12-30 17:56:20');
