INSERT INTO `doulist` VALUES ('12896', '林文月', '0', 'murongxiaosong', '29', '0', '<p class=\"indent\">　　   林文月，臺灣彰化北斗人，誕生於上海日本租界，啟蒙教育為日文，至小學六年級返歸臺灣，始接受中文教育，故自然通曉中、日語文。自大學時期即從事中、日文學翻譯工作。曾任日本京都大學人文科學研究所研修員、美國西雅圖華盛頓大學、史丹佛大學、加州柏克萊大學、捷克查理斯大學客座教授。一九五八年至一九九三年在臺灣大學中文系任教時，專攻六朝文學、中日比較文學，並曾教授現代散文等課。一九九三年退休，次年獲聘為臺大中文系名譽教授。<br /></p>', '2006-08-19 00:32:28', '0000-00-00 00:00:00');
