INSERT INTO `doulist` VALUES ('16327', '小霉女的豆列书', '-1', 'coutiy', '4', '0', '<p class=\"indent\">　　这世界太多的不解\r<br />　　浪费时间在银幕的面前\r<br />　　我和你有一点距离\r<br />　　这种危险悄悄的在上演\r<br />　　\r<br />　　也许我们太专注眼前\r<br />　　那些无是生非的支节\r<br />　　情话却忘了多说一些\r<br />　　蓝色绿色橘色红色\r<br />　　杀人放火其实关我们屁事\r<br />　　我爱你我的baby\r<br />　　\r<br />　　我要摔坏电视机\r<br />　　我要紧紧拥抱你\r<br />　　对不起冷落彼此\r<br />　　是我该死是我忽略\r<br />　　我要摔烂电视机\r<br />　　我要带你去旅行\r<br />　　飞向那未知国度\r<br />　　无人岛里只有我和你\r<br />　　我和你\r<br />　　<br /></p>', '2006-10-11 11:27:15', '0000-00-00 00:00:00');
