INSERT INTO `doulist` VALUES ('16330', '小霉女的豆列沐维戏', '-1', 'coutiy', '0', '0', '<p class=\"indent\">　　北方的马蹄弥漫着雪白的过去\r<br />　　这整遍银白色的大地凝结空气\r<br />　　而我从梦中苏醒还在起伏情绪\r<br />　　一次次计算梦见你的机率\r<br />　　古老的村庄传说总是特别神秘\r<br />　　有一些年代的爱像诗美如瓷器\r<br />　　我路过小镇留下思念你的伏笔\r<br />　　只为了等待那赤年后的相遇\r<br />　　啦～啦～\r<br />　　相恋的雨季长满了诗句\r<br />　　你摘下我的语气培养成秘密\r<br />　　风很轻爬上蜿蜒的阶梯\r<br />　　山神的庙走进去\r<br />　　我决定神秘的爱你\r<br />　　广场热闹庆典很华丽\r<br />　　你绕我跳圆舞曲\r<br />　　我确定跟你的默契\r<br />　　扭腰摆手精准的比例\r<br />　　我们完美跳到底\r<br />　　我约定再回来这里\r<br />　　山谷传来幽雅的风笛\r<br />　　唤醒有你的记忆\r<br />　　我坚定前世在一起\r<br />　　\r<br />　　<br /></p>', '2006-10-11 11:33:31', '0000-00-00 00:00:00');
