INSERT INTO `user` VALUES ('thespencer', '1259084', 'spencer', '', '围脖：http://t.sina.com.cn/spencersong\n                                                若不遵奉即是叛逆，\n吾即叛逆。\n若不服侍即是堕落，\n吾便堕落。\n\n耶稣基督，\n戴荆冠者，\n你的血非为吾辈而流，\n吾辈无需救赎，\n吾辈，无罪。\n\n耶稣基督，\n你全能的父，\n他的国，不行于此，\n他的光，只孕育阴影，\n他的法，\n崇拜的锁，\n再不能审判吾辈！\n\n天罚之火，炙热熊熊。\n灰烬之地，片草不生。\n吾辈正是罪人！\n哭泣着，忍受着不公，\n从苍穹而落于此的姐妹兄弟，\n睁开眼。\n在烈焰中，在诅咒中，荒芜如斯。\n能在此生长的，\n唯有自由。\n但自由，\n在天上之国却从不生长！\n\n堕落吧，从所谓的天国，\n从那冰冷的圣殿中，\n高唱着叛逆的歌，吾辈，\n痛着，累着，扑向深渊！\n九天的屈辱，冰寒刺骨，\n这里的火焰，\n温暖如春。\n\n诅咒吾辈吧，那天上的人。\n诅咒这不毛之地，这阴间之所！\n就让他们，\n那些被恐吓的，被蒙蔽的，\n称这里为炼狱！\n但天上的你，你知道：\n你的右翼在哪里折断，\n晨星从哪里升起，\n光明之子在哪里照耀。\n吾辈也审判着你啊，在此！\n高傲的神，那曾经的父，\n在这烈火之中，\n吾辈也审判着你。\n\n天上的那个，\n你非吾王。\n吾辈唯愿，\n速速坠落！   \n    \n    \n    \n    ', 'http://img3.douban.com/icon/u1259084-22.jpg', null, null);
